import React, { useState, useEffect } from 'react';

const AudioPlayer = ({ audioFile }) => {
  const [audioSource, setAudioSource] =  useState({ audioFile, uniqueId: Date.now() });

  useEffect(() => {
    setAudioSource({ audioFile, uniqueId: Date.now() });
  }, [audioFile]);

  return (
    <div>
      <audio controls>
        <source src={audioSource.audioFile} type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>
    </div>
  );
};

export default AudioPlayer;
